import jxl.*
import jxl.write.*
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.support.GroovyUtils
import groovy.json.JsonSlurper
import com.eviware.soapui.impl.wsdl.WsdlProject

Workbook wb = Workbook.getWorkbook(new File("E:\\Soap\\DataDriven.xls"))
Sheet sheet = wb.getSheet(0)
size = sheet.getRows();
prop = sheet.getColumns();
for(int i=0;i<size;i++){
	for(int j=0;j<prop;j++){
		if(sheet.getCell(j,i).getContents()=='create_user'){
			testRunner.testCase.testSuite.getTestCaseByName("user").getTestStepByName("user").setPropertyValue('Request',sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='get_user_by_name'){
			testRunner.testCase.testSuite.getTestCaseByName("Get by username").getTestStepByName("Properties").setPropertyValue('username',sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='email'){
			testRunner.testCase.testSuite.getTestCaseByName("Login").getTestStepByName("Properties").setPropertyValue("email",sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='password'){
			testRunner.testCase.testSuite.getTestCaseByName("Login").getTestStepByName("Properties").setPropertyValue("password",sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='update_user'){
			testRunner.testCase.testSuite.getTestCaseByName("Update").getTestStepByName("Properties").setPropertyValue("username",sheet.getCell(j+1,i).getContents())
			testRunner.testCase.testSuite.getTestCaseByName("Update").getTestStepByName("UpdateUser").setPropertyValue("Request",sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='create_user_with_array'){
			testRunner.testCase.testSuite.getTestCaseByName("CreateUserArray").getTestStepByName("CreateUserArray").setPropertyValue("Request",sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='create_user_with_list'){
			testRunner.testCase.testSuite.getTestCaseByName("CreateUserByList").getTestStepByName("CreateUserByList").setPropertyValue("Request",sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='delete_user'){
			testRunner.testCase.testSuite.getTestCaseByName("DeleteUser").getTestStepByName("Properties").setPropertyValue("username",sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='place_order'){
			testRunner.testCase.testSuite.getTestCaseByName("PlaceOrder").getTestStepByName("PlaceOrder").setPropertyValue("Request",sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='get_order_by_id'){
			testRunner.testCase.testSuite.getTestCaseByName("GetOrderById").getTestStepByName("Properties").setPropertyValue("order_id",sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='delete_order_by_id'){
			testRunner.testCase.testSuite.getTestCaseByName("DeleteOrderByID").getTestStepByName("Properties").setPropertyValue("order_id",sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='add_new_pet'){
			testRunner.testCase.testSuite.getTestCaseByName("AddNewPet").getTestStepByName("AddNewPet").setPropertyValue("Request",sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='upload_image_pet'){
			testRunner.testCase.testSuite.getTestCaseByName("UploadImage").getTestStepByName("Properties").setPropertyValue("petid",sheet.getCell(j+1,i).getContents())
			testRunner.testCase.testSuite.getTestCaseByName("UploadImage").getTestStepByName("Properties").setPropertyValue("file",sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='update_pet'){
			testRunner.testCase.testSuite.getTestCaseByName("UpdatePet").getTestStepByName("UpdatePet").setPropertyValue("Request",sheet.getCell(j+2,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='get_pet_status'){
			testRunner.testCase.testSuite.getTestCaseByName("GetPetByStatus").getTestStepByName("Properties").setPropertyValue("petstatus",sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='get_pet_by_id'){
			testRunner.testCase.testSuite.getTestCaseByName("GetPetById").getTestStepByName("Properties").setPropertyValue("petid",sheet.getCell(j+1,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='update_pet_by_id'){
			testRunner.testCase.testSuite.getTestCaseByName("UpdatePetByID").getTestStepByName("Properties").setPropertyValue("petid",sheet.getCell(j+1,i).getContents())
			testRunner.testCase.testSuite.getTestCaseByName("UpdatePetByID").getTestStepByName("Properties").setPropertyValue("name",sheet.getCell(j+2,i).getContents())
			testRunner.testCase.testSuite.getTestCaseByName("UpdatePetByID").getTestStepByName("Properties").setPropertyValue("status",sheet.getCell(j+3,i).getContents())
		}else if(sheet.getCell(j,i).getContents()=='delete_pet_by_id'){
			testRunner.testCase.testSuite.getTestCaseByName("DeletePetById").getTestStepByName("Properties").setPropertyValue("petid",sheet.getCell(j+1,i).getContents())
		}
	}
}

